import React, { useCallback, useMemo } from 'react';
import PropTypes from 'prop-types';
import { List, Button, Card } from 'antd';
import { StopOutlined } from '@ant-design/icons';
import styled  from 'styled-components';

const Flist = styled(List)`
  margin-bottom: 20px;
`;
const ListItem = styled(List.Item)`
  margin-top: 20px;
`;
const ButtonWrapper = styled.div`
  text-align: center;
  margin: 10px 0;
`;

const FollowList = ({ header, data }) => {
  const Grid = useMemo(() => ({ gutter: 4, column: 3 }), []);
  const ListHeader = useMemo(() => <div>{header}</div>, []);
  const MoreButton = useMemo(() => <ButtonWrapper><Button>더 보기</Button></ButtonWrapper>, []);
  const RenderItem = useCallback((item) => (
    <ListItem>
      <Card actions={[<StopOutlined key="stop" />]}>
        <Card.Meta description={item.nickname} />
      </Card>
    </ListItem>
  ), [item.nickname]);
  // const Parent = ({ a }) => useMemo(() => <Child1 a={a} />, [a]); 


  return (
    // 객체 넣은 것들 전부 최적화 해주어야한다. 아래처럼 그냥 넣으면 안됨
    <Flist
      grid={Grid}
      size="small"
      header={ListHeader}
      loadMore={MoreButton}
      bordered
      dataSource={data}
      renderItem={RenderItem}
    />
  );
};

FollowList.propTypes = {
  header: PropTypes.string.isRequired,
  data: PropTypes.array.isRequired,
}

export default FollowList;
